import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CurrentStaticDataService } from 'src/app/Services/CurrentStaticData/current-static-data.service';
import { FunctionalitiesService } from 'src/app/Services/Functionalities/functionalities.service';

@Component({
  selector: 'app-add-functionality-modal',
  templateUrl: './add-functionality-modal.component.html',
  styleUrls: ['./add-functionality-modal.component.scss']
})
export class AddFunctionalityModalComponent {

  @Output() addFunctionality = new EventEmitter<any>();

  statusOptions: string[] = ["todo", "doing", "done"];
  priorityOptions: string[] = ["low", "medium", "high"];
  isModalOpen: boolean = false;
  currentProject;

  constructor(private currentStaticDataService: CurrentStaticDataService) {}

  generateId() {
    return new Date().getTime().toString();
  }

  ngOnInit() {
    this.currentProject = this.currentStaticDataService.getCurrentProject();
  }

  newFunctionality: any = {
    id: this.generateId(),
    title: '',
    description: '',
    priority: 'low',
    status: 'todo',
    project_id: ''
  };

  onCloseClick() {
    this.isModalOpen = false;
  }

  onSaveClick() {
    this.isModalOpen = false;
    this.addFunctionality.emit(this.newFunctionality);
    this.newFunctionality = {
      id: '',
      title: '',
      description: '',
      priority: '',
      status: 'todo',
      project_id: ''
    };
  }

  openAddFunctionalityModal() {
    this.isModalOpen = true;
  }
}
<button class="mui-button" (click)="openAddFunctionalityModal()">Add</button>

<div class="modal" *ngIf="isModalOpen">
  <div class="modal-overlay"></div>
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h4 class="modal-title">Add Functionality</h4>
      </div>
      <div class="modal-body">
        <div class="form-group">
          <label class="mui-label">Title</label>
          <input class="mui-input" type="text" [(ngModel)]="newFunctionality.title">
        </div>
        <div class="form-group">
          <label class="mui-label">Description</label>
          <textarea class="mui-input" [(ngModel)]="newFunctionality.description"></textarea>
        </div>
        <div class="form-group">
          <label class="mui-label">Priority</label>
          <select class="mui-select" [(ngModel)]="newFunctionality.priority" name="priority">
            <option *ngFor="let option of priorityOptions" [value]="option">{{ option }}</option>
          </select>
        </div>
        <div class="form-group">
          <label class="mui-label">Status</label>
          <select class="mui-select" [(ngModel)]="newFunctionality.status" name="status">
            <option *ngFor="let option of statusOptions" [value]="option">{{ option }}</option>
          </select>
        </div>
        <div class="form-group">
          <label class="mui-label">Project</label>
          <select class="mui-select" [(ngModel)]="newFunctionality.project_id">
            <option [value]="currentProject.id">{{ currentProject.name }}</option>
          </select>
        </div>
      </div>
      <div class="modal-footer">
        <button class="mui-button" (click)="onCloseClick()">Close</button>
        <button class="mui-button mui-button--primary" (click)="onSaveClick()">Save</button>
      </div>
    </div>
  </div>
</div>
