import { Component } from '@angular/core';
import { FunctionalitiesService } from 'src/app/Services/Functionalities/functionalities.service';

@Component({
  selector: 'app-functionalities-board',
  templateUrl: './functionalities-board.component.html',
  styleUrls: ['./functionalities-board.component.scss']
})
export class FunctionalitiesBoardComponent {
  constructor(private funcService: FunctionalitiesService) { }

  functionalities;
  selectedFunctionality;

  ngOnInit() {
    this.functionalities = this.funcService.getFunctionalities();
  }

  deleteFunctionality(functionalityId: string) {
    console.log(functionalityId);
    this.funcService.deleteFunctionality(functionalityId);
    this.functionalities = this.funcService.getFunctionalities();
  }

  onSaveFunctionality(updatedFunctionality: any) {
    console.log(updatedFunctionality);
    this.funcService.updateFunctionality(updatedFunctionality);
    this.functionalities = this.funcService.getFunctionalities();
  }

  openFunctionalityModal(functionality) {
    console.log(functionality);
    this.selectedFunctionality = functionality;
  }

  closeFunctionalityModal() {
    this.selectedFunctionality = null;
    console.log(this.selectedFunctionality)
  }
}
<div>
    <div class="container">
      <div class="col-md-4" *ngFor="let item of functionalities">
        <div *ngIf="item.status == 'todo'">
            <app-functionality-card [id]="item.id" [title]="item.title" [status]="item.status" backgroundColor="#ddd" (deleteFunctionality)="deleteFunctionality($event)" (click)="openFunctionalityModal(item)"></app-functionality-card>
        </div>
      </div>
      <div class="col-md-4" *ngFor="let item of functionalities">
        <div *ngIf="item.status == 'doing'">
            <app-functionality-card [id]="item.id" [title]="item.title" [status]="item.status" backgroundColor="blue" (deleteFunctionality)="deleteFunctionality($event)" (click)="openFunctionalityModal(item)"></app-functionality-card>
        </div>
      </div>
      <div class="col-md-4" *ngFor="let item of functionalities">
        <div *ngIf="item.status == 'done'">
            <app-functionality-card [id]="item.id" [title]="item.title" [status]="item.status" backgroundColor="green" (deleteFunctionality)="deleteFunctionality($event)" (click)="openFunctionalityModal(item)"></app-functionality-card>
        </div>
      </div>
    </div>
</div>
