import { Component } from '@angular/core';
import { TasksService } from 'src/app/Services/Tasks/tasks.service';


@Component({
  selector: 'app-task-board',
  templateUrl: './task-board.component.html',
  styleUrls: ['./task-board.component.scss']
})


export class TaskBoardComponent {
  constructor(private taskService: TasksService) { }

  tasks;
  selectedTask;
  task;
  functionalityList: any[];
  currentUser;

  deleteTask(taskId: string) {
    console.log(taskId);
    this.taskService.deleteTask(taskId);
    this.tasks = this.taskService.getTasks();
  }

  onSaveTask(updatedTask: any) {
    console.log(updatedTask);
    this.taskService.updateTask(updatedTask);
    this.tasks = this.taskService.getTasks();
  }

  onAddTask(newTask: any) {
    console.log(newTask);
    this.taskService.addTask(newTask);
    this.tasks = this.taskService.getTasks();
  }

  ngOnInit() {
    this.tasks = this.taskService.getTasks();
    this.currentUser = this.currentStaticDataService.getCurrentUser();
  }

  openTaskModal(task) {
    console.log(task);
    this.selectedTask = task;
  }

  closeTaskModal() {
    this.selectedTask = null;
    console.log(this.selectedTask)
  }

  checkUser(functionalityId: string) {
    let functionality = this.funcService.getFunctionality(functionalityId);
    console.log(parseInt(functionality.assigned_to));
    console.log(this.currentUser.id);
    if (functionality.assigned_to == this.currentUser.id) {
      return this.currentUser.FirstName + " " + this.currentUser.LastName;
    } else {
      return "";
    }
  }
}
<app-add-task-modal (addTask)="onAddTask($event)"></app-add-task-modal>
<div>
    <div class="container">
      <div class="col-md-4" *ngFor="let item of tasks">
        <div *ngIf="item.status == 'todo'">
            <app-task-card [id]="item.id" [title]="item.title" [status]="item.status" [functionality_id]="item.functionality_id" backgroundColor="#ddd" [assignedTo]="checkUser(item.id)" (deleteTask)="deleteTask($event)" (click)="openTaskModal(item)"></app-task-card>
        </div>
      </div>
      <div class="col-md-4" *ngFor="let item of tasks">
        <div *ngIf="item.status == 'doing'">
            <app-task-card [id]="item.id" [title]="item.title" [status]="item.status" [functionality_id]="item.functionality_id" backgroundColor="blue" [assignedTo]="checkUser(item.id)" (deleteTask)="deleteTask($event)" (click)="openTaskModal(item)"></app-task-card>
        </div>
      </div>
      <div class="col-md-4" *ngFor="let item of tasks">
        <div *ngIf="item.status == 'done'">
            <app-task-card [id]="item.id" [title]="item.title" [status]="item.status" [functionality_id]="item.functionality_id" backgroundColor="green" [assignedTo]="checkUser(item.id)" (deleteTask)="deleteTask($event)" (click)="openTaskModal(item)"></app-task-card>
        </div>
      </div>
    </div>
</div>

<app-task-modal *ngIf="selectedTask" [taskId]="selectedTask.id" (closeTaskModal)="closeTaskModal()" (saveTask)="onSaveTask($event)"></app-task-modal>
