import { Component, Input } from '@angular/core';
import { DataService } from '../data.service';
import { Task } from '../task';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent {
  @Input() task: Task;
  @Input() functionalityId: number;

  isEditing: boolean = false;
  updatedTask: Task;

  constructor(private dataService: DataService) { }

  toggleEdit() {
    this.isEditing = !this.isEditing;
    if (this.isEditing) {
      // Make a copy of the task to preserve the original data
      this.updatedTask = { ...this.task };
    }
  }

  updateTask() {
    this.dataService.updateTask(this.functionalityId, this.updatedTask).subscribe(
      (res: Task) => {
        this.task = res;
        this.toggleEdit();
      },
      error => {
        console.log(error);
      }
    );
  }

  deleteTask() {
    this.dataService.deleteTask(this.functionalityId, this.task.id).subscribe(
      () => {
        // Optionally handle success
      },
      error => {
        console.log(error);
      }
    );
  }
}
