import { Component, Input, Output, EventEmitter } from '@angular/core';
import { FunctionalitiesService } from 'src/app/Services/Functionalities/functionalities.service';
import { TasksService } from 'src/app/Services/Tasks/tasks.service';

@Component({
  selector: 'app-task-modal',
  templateUrl: './task-modal.component.html',
  styleUrls: ['./task-modal.component.scss']
})
export class TaskModalComponent {
  @Input() taskId;
  @Input() functionalityList;
  task;
  Date: Date;

  statusOptions: string[] = ["todo", "doing", "done"];
  priorityOptions: string[] = ["low", "medium", "high"];

  @Output() closeTaskModal = new EventEmitter<void>();
  @Output() saveTask = new EventEmitter<any>();

  constructor(private taskService: TasksService, private funcService: FunctionalitiesService) {}

  ngOnInit() {
    this.Date = new Date();
    this.task = this.taskService.getTaskById(this.taskId);
    this.functionalityList = this.funcService.getFunctionalities();
  }

  onCloseClick() {
    this.closeTaskModal.emit();
  }

  onSaveClick() {
    console.log(this.task);
    this.saveTask.emit(this.task);
    this.closeTaskModal.emit();
  }

  onStatusChange() {
    if (this.task.status === 'doing') {
      this.task.started = this.Date.toISOString();
      this.funcService.updateFunctionalityStatus(this.task.functionality_id, 'doing');
    }
    if (this.task.status === 'todo') {
      this.task.started = null;
      this.task.end_date = null;
    }
    if (this.task.status === 'done') {
      this.task.end_date = this.Date.toISOString();
      this.checkifalltasksdone();
    }
  }

  checkifalltasksdone() {
    const functionality = this.funcService.getFunctionalityById(this.task.functionality_id);
    console.log('func: '+ functionality)
    const tasks = this.taskService.getRelatedTasks(this.task.functionality_id);
    console.log('tasks: '+ tasks)
    let allTasksDone = true;
    for (const task of tasks) {
      if (task.status !== 'done') {
        allTasksDone = false;
      }
    }
    if (allTasksDone) {
      functionality.status = 'done';
      this.funcService.updateFunctionalityStatus(this.task.functionality_id, 'done');
    }
  }
}
<div class="modal modal-overlay">
  <div class="modal-content">
    <div class="modal-header">
      <h4 class="modal-title">Task Details</h4>
    </div>
    <div class="modal-body">
      <div class="form-group">
        <label>Title</label>
        <input type="text" class="form-control" [(ngModel)]="task.title">
      </div>
      <div class="form-group">
        <label>Description</label>
        <textarea class="form-control" [(ngModel)]="task.description"></textarea>
      </div>
      <div class="form-group">
        <label>Priority</label>
        <select id="priority" class="form-control status-dropdown" [(ngModel)]="task.priority" name="priority">
          <option *ngFor="let option of priorityOptions" [value]="option">{{ option }}</option>
        </select>
      </div>
      <div class="form-group">
        <label>Estimate</label>
        <input type="text" class="form-control" [(ngModel)]="task.estimated_time">
      </div>
      <div class="form-group">
        <label for="status">Status:</label>
        <select id="status" class="form-control status-dropdown" [(ngModel)]="task.status" name="status" (change)="onStatusChange()">
          <option *ngFor="let option of statusOptions" [value]="option">{{ option }}</option>
        </select>
      </div>
      <div class="form-group">
        <label>Created</label>
        <input type="text" class="form-control" [(ngModel)]="task.created_at" [disabled]="true">
      </div>
      <div class="form-group">
        <label for="started">Started</label>
        <input id="started" class="form-control" [(ngModel)]="task.started" name="started" [disabled]="true">
      </div>
      <div class="form-group">
        <label>Completed</label>
        <input type="text" class="form-control" [(ngModel)]="task.end_date" [disabled]="true">
      </div>
      <div>
        <label>Functionality:</label>
        <select [(ngModel)]="task.functionality_id">
          <option *ngFor="let functionality of functionalityList" [value]="functionality.id">
            {{ functionality.title }}
          </option>
        </select>
      </div>
    </div>
    <div class="modal-footer">
      <button type="button" class="btn btn-primary" (click)="onCloseClick()">Close</button>
      <button type="button" class="btn btn-primary" (click)="onSaveClick()">Save</button>
    </div>
  </div>
</div>
