import { Component, OnInit } from '@angular/core';
import { DataService } from '../data.service';
import { Functionality } from '../functionality';

@Component({
  selector: 'app-functionality',
  templateUrl: './functionality.component.html',
  styleUrls: ['./functionality.component.scss']
})
export class FunctionalityComponent implements OnInit {
  functionalities!: Functionality[];

  constructor(private dataService: DataService) { }

  ngOnInit() {
    this.loadFunctionalities();
  }

  loadFunctionalities() {
    this.dataService.getFunctionalities().subscribe(
      (res: Functionality[]) => {
        this.functionalities = res;
      },
      error => {
        console.log(error);
      }
    );
  }

  createFunctionality(name: string) {
    const functionality: Functionality = {
      name: name,
      tasks: []
    };

    this.dataService.createFunctionality(functionality).subscribe(
      (res: Functionality) => {
        this.functionalities.push(res);
      },
      error => {
        console.log(error);
      }
    );
  }

  deleteFunctionality(functionalityId: number) {
    this.dataService.deleteFunctionality(functionalityId).subscribe(
      () => {
        this.functionalities = this.functionalities.filter(
          functionality => functionality.id !== functionalityId
        );
      },
      error => {
        console.log(error);
      }
    );
  }
}
