import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { TodoService } from '../todo.service';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.scss']
})
export class TodosComponent implements OnInit {

  todos;
  id;
  text;
  title;
  oldText;
  oldTitle;
  appState = 'default';

  constructor(private todoService: TodoService) { }

  ngOnInit() {
    this.todos = this.todoService.getTodos();
  }

  generateID(): number {
    const timestamp = Date.now();
    return timestamp;
  }

  addTodo() {
    let newTodo = {
      id: this.generateID(),
      text: this.text,
      title: this.title
    }
    this.todos.push(newTodo);

    this.todoService.addTodo(newTodo);
  }

  deleteTodo(todoID) {
    for(let i = 0; i < this.todos.length; i++) {
      if(this.todos[i].id == todoID) {
          this.todos.splice(i, 1);
      }
    }

    this.todoService.deleteTodo(todoID);
  }

  editTodo(todo) {
    console.log(todo);
    this.appState = 'edit';
    this.id = todo.id;
    this.oldText = todo.text;
    this.text = todo.text;
    this.oldTitle = todo.title;
    this.title = todo.title;
  }

  updateTodo() {
    console.log(this.id, this.text);
        for(let i = 0; i < this.todos.length; i++) {
          if(this.todos[i].text == this.oldText) {
            this.todos[i].text = this.text;
          }
          if(this.todos[i].title == this.oldTitle) {
            this.todos[i].title = this.title;
          }
        }
    this.todoService.updateTodo(this.oldText, this.text, this.oldTitle, this.title);
  }

}
