import { Component } from '@angular/core';
import { FunctionalitiesService } from 'src/app/Services/Functionalities/functionalities.service';

@Component({
  selector: 'app-functionalities-board',
  templateUrl: './functionalities-board.component.html',
  styleUrls: ['./functionalities-board.component.scss']
})
export class FunctionalitiesBoardComponent {
  constructor(private funcService: FunctionalitiesService) { }

  functionalities;

  ngOnInit() {
    this.functionalities = this.funcService.getFunctionalities();
  }
}
<div>
    <div class="container">
      <div class="col-md-4" *ngFor="let item of functionalities">
        <div *ngIf="item.status == 'todo'">
            <app-functionality-card [id]="item.id" [title]="item.title" [status]="item.status" backgroundColor="#ddd" (deleteFunctionality)="deleteFunctionality($event)"></app-functionality-card>
        </div>
      </div>
      <div class="col-md-4" *ngFor="let item of functionalities">
        <div *ngIf="item.status == 'doing'">
            <app-functionality-card [id]="item.id" [title]="item.title" [status]="item.status" backgroundColor="blue" (deleteFunctionality)="deleteFunctionality($event)"></app-functionality-card>
        </div>
      </div>
      <div class="col-md-4" *ngFor="let item of functionalities">
        <div *ngIf="item.status == 'done'">
            <app-functionality-card [id]="item.id" [title]="item.title" [status]="item.status" backgroundColor="green" (deleteFunctionality)="deleteFunctionality($event)"></app-functionality-card>
        </div>
      </div>
    </div>
</div>
