import { Component, Input, Output, EventEmitter } from '@angular/core';
import { TasksService } from 'src/app/Services/Tasks/tasks.service';

@Component({
  selector: 'app-task-card',
  templateUrl: './task-card.component.html',
  styleUrls: ['./task-card.component.scss'],
  standalone: true
})

export class TaskCardComponent {
  @Input() id: string;
  @Input() title: string;
  @Input() status: string;
  @Input() backgroundColor;
  @Input() functionalityTitle: string;

  constructor(private taskService: TasksService) { }

  getFunctionalityTitle() {
    return this.taskService.getFunctionalityTitle(this.id);
  }

  ngOnInit() {
    this.functionalityTitle = this.taskService.getFunctionalityTitle(this.id);
  }

  @Output() deleteTask = new EventEmitter<string>();
  onDeleteClick() {
    this.deleteTask.emit(this.id);
  }
}
