import { Injectable } from '@angular/core';
import { Init } from './Inits/init-tasks';

@Injectable({
  providedIn: 'root'
})

export class TasksService extends Init{

  constructor() { 
    super();
    this.load();
  }

  getTasks() {
    let tasks = JSON.parse(localStorage.getItem('tasks'));
    return tasks;
  }
}
